import React,{Component} from 'react'
import axios from 'axios'

class SearchPage extends Component{
    getResult(){
        let kw = this.input.value;
        console.log(kw);
        // https://wap.chinaticket.com/wapapi/searchshows.do
        // ?cityid=1&kw=%E9%9F%B3%E4%B9%90&page=1&sign=1ef59d37454f6ea3ab&time=1544148448
        axios.get('/myapi/wapapi/searchshows.do',{
			params:{
                cityid:1,
                kw:kw,
                page:1,
				sign:'1ef59d37454f6ea3ab',
				time:1544148448
			}
		}).then(res=>{
            let data = res.data.datas;
            console.log(data)  
			this.setState({
				resu:data,
            });
        });
    }
    handleEnterKey = (e) => {
        if(e.nativeEvent.keyCode === 13){
             this.getResult()
        }
    }
    render(){
        return <div className="searchPage">
            <input type="text" ref={input => this.input = input} onKeyPress={this.handleEnterKey} placeholder="请输入演出名称"/>
            <button onClick={this.getResult.bind(this)}>搜索</button>
        </div>
    }
}

export default SearchPage
