import React,{Component} from 'react';
import { Carousel } from 'antd-mobile';
import axios from 'axios';

class Banner extends Component {
    constructor(){
        super();
        this.state = {
            //轮播图商品
            ad:[],
            //列表
            goodslist:[]
        }
    }
    componentWillMount(){
         // https://wap.chinaticket.com/wapapi/hdpic.do?cityid=1&sign=bf3e441d0b83f49f2d&time=1543724998
		axios.get('/myapi/wapapi/hdpic.do?cityid=1&sign=bf3e441d0b83f49f2d&=1543724998',{
			params:{
				t:Date.now(),
				pagenum:1,
				tabnum:1
			}
		}).then(res=>{
			let data = res.data;
			this.setState({
				ad:data.killProList.slice(0,4),
				goodslist:data.killProList.slice(4)
			});
        });
    }
    render() {
      return (
        <Carousel
        autoplay={true}
        infinite
        >
        {this.state.ad.map(goods => (
            <a
            key={goods.proId}
            href="#"
            style={{height:'320px'}}
            >
            <img
                src={goods.proImg}
                style={{ width: '100%', height:'200px', verticalAlign: 'top' }}
                onLoad={() => {
                    window.dispatchEvent(new Event('resize'));
                }}
            />
            </a>
        ))}
        </Carousel>
      );
    }
  }

export default Banner;
