import React,{Component} from 'react'
import axios from 'axios'

class SearchPage extends Component{
    
    getResult(){
        let kw = this.input.value;
        console.log(kw);
        axios.get('/myapi/wapapi/searchshows.do',{
			params:{
                cityid:1,
                kw:'%E9%9F%B3%E4%B9%90',
				sign:'1ef59d37454f6ea3ab',
				time:1543729843
			}
		}).then(res=>{
            let data = res.data;
            console.log(data)  
			this.setState({
				hotTicket:data,
            });
        });
    }
    render(){
        return <div className="searchPage">
            <input type="text" ref={input => this.input = input} onChange={this.getResult.bind(this)} placeholder="请输入演出名称"/>
            <button onClick={this.getResult.bind(this)}>搜索</button>
        </div>
    }
}

export default SearchPage
