import React,{Component} from 'react';
import { Carousel } from 'antd-mobile';
import axios from 'axios';

class Banner extends Component {
    constructor(){
        super();
        this.state = {
            //轮播图商品
            ad:{},
        }
    }
    componentWillMount(){
         // https://wap.chinaticket.com/?
		axios.get('/myapi/wapapi/hdpic.do',{
			params:{
                cityid:1,
				sign:'bb4284fa771000e8c0',
				time:1543729843
			}
		}).then(res=>{
            console.log(res)
            let data = res.data;
            
			this.setState({
				ad:data,
			});
        });
    }
    render() {
           {console.log(this.state.ad)}
      return (
        <Carousel
        autoplay={true}
        infinite
        >
     
        {this.state.ad.map(goods => (
            <a
            key={goods.proId}
            style={{height:'320px'}}
            >
            <img
                src={goods.responseURL                }
                style={{ width: '100%', height:'200px', verticalAlign: 'top' }}
                onLoad={() => {
                    window.dispatchEvent(new Event('resize'));
                }}
            />
            </a>
        ))}
        </Carousel>
      );
    }
  }

export default Banner;
