import React,{ Component } from 'react'
import { Accordion, List } from 'antd-mobile';
import {withRouter} from 'react-router-dom'

import {connect} from 'react-redux';


import '../../style/common.scss'


class Nav extends Component {
    
    constructor(){
        super();
        this.state = {
            con:[
                '演唱会','音乐会/歌剧','话剧/舞台剧','音乐剧','戏曲曲艺','芭蕾舞/舞蹈',
                '相声/小品','马戏/杂技/魔术','综艺节目','亲子家庭'
            ],
            nav:[
                {title:'文艺演出',con:[
                    '演唱会','音乐会/歌剧','话剧/舞台剧','音乐剧','戏曲曲艺','芭蕾舞/舞蹈',
                    '相声/小品','马戏/杂技/魔术','综艺节目','亲子家庭'
                ]},
                {title:'体育赛事',con:[
                    '演唱会','音乐会/歌剧','话剧/舞台剧','音乐剧','戏曲曲艺','芭蕾舞/舞蹈',
                    '相声/小品'
                ]},
                {title:'其他',con:[
                    '演唱会','音乐会/歌剧','话剧/舞台剧','音乐剧','戏曲曲艺','芭蕾舞/舞蹈'
                ]},
            ]
        }
    }

    componentWillMount(){
        console.log(this.props.showStatus)
    }

    gotopage = (key) => {
        console.log(key);
      }
    render(){
        // let navCon = this.state.nav 
        let con = this.state.con   
        return (
            <div className="nav" style={{display:this.props.showStatus?'block':'none'}}>
                <div className="navList">
                <Accordion defaultActiveKey="0" className="my-accordion" onChange={this.gotopage}>
                    <Accordion.Panel header="首页"></Accordion.Panel>
                    <Accordion.Panel header="北京"></Accordion.Panel>
                    <Accordion.Panel header="文艺演出">
                        <List className="my-list">
                            {con.map(con=>{
                                return  <List.Item>{con}</List.Item>                                         
                            })}
                        </List>
                    </Accordion.Panel>
                    <Accordion.Panel header="体育赛事">
                        <List className="my-list">
                            {con.map(con=>{
                                return  <List.Item>{con}</List.Item>                                         
                            })}
                        </List>
                    </Accordion.Panel>
                    <Accordion.Panel header="其他">
                        <List className="my-list">
                            {con.map(con=>{
                                return  <List.Item>{con}</List.Item>                                         
                            })}
                        </List>
                    </Accordion.Panel>
                
                </Accordion>
                </div>
                <div className="navBtn">
                    <button>登录</button>
                    <p>客服电话：400-880-2880</p>
                </div>
            </div>
        )
    }
}


let mapStateToProps=state=>({showStatus:state.commonReducer.shownavStatus});
Nav = connect(mapStateToProps)(Nav);

Nav = withRouter(Nav)
export default Nav;
