import React, { Component } from 'react';
import axios from 'axios'

import '../../../style/tacketList.scss'

class TacketList extends Component {
    constructor(){
        super();
        this.state = {
            //热门门票
            hotTicket:[],
        }
    }
    componentWillMount(){
		axios.get('/myapi/wapapi/hbztj.do',{
			params:{
                cityid:1,
				sign:'bb4284fa771000e8c0',
				time:1543729843
			}
		}).then(res=>{
            let data = res.data;
            console.log(data)
			this.setState({
				hotTicket:data,
			});
        });
    }
  render() {
    return (   
      <div className="tacketList">

<Grid
            data={this.state.goodslist} 
            columnNum={2} 
            activeClassName="active" 
            itemStyle={{height:'260px'}}
            renderItem={(goods,idx)=>{
                return(
                    <div className="goods-item">
                        <img src={goods.proImg} />
                        <h4>{goods.proName}</h4>
                        <p className="price">原价：<del>{goods.proPrice.toFixed(2)}</del></p>
                        <p className="price">现价：<span>{(goods.proPrice*goods.sellPercent/100).toFixed(2)}</span></p>
                    </div>
                )
            }}
            onClick={this.handlerGotoDetails}
            />

        <div className="listTitle">
            <i></i>
            <h3>热门</h3>
        </div>
        <div className="listInfo">
            <ul>
                <li>
                    <img src="{hotTicket.thumbsrc" className="fl" />
                    <div className="info fl">
                        <h4>{title}</h4>
                        <p>{city}</p>
                        <p>{price}</p>
                        <span className="disc">售票中</span>
                    </div>
                </li>
            </ul>
        </div>
      </div>
    );
  }
}

export default TacketList;
