import React,{Component} from 'react'
import axios from 'axios'

class SearchPage extends Component{
    constructor(props){
        this.kw = React.createRef()
      }
    getResult(){
        let kw = this.kw.current.input.value
        console.log(kw);
        if(!val.trim()){
            message.warn('请输入搜索内容后再点击搜索按钮')
            return false
        }
        //这里是触发查询的接口
        this.props.matchHistory.getHistoryList(val)

       
        axios.get('/myapi/wapapi/searchshows.do',{
			params:{
                cityid:1,
                kw:'%E9%9F%B3%E4%B9%90',
				sign:'1ef59d37454f6ea3ab',
				time:1543729843
			}
		}).then(res=>{
            let data = res.data;
            console.log(data)  
			this.setState({
				hotTicket:data,
            });
        });
    }
    render(){
        return <div className="searchPage">
            <input type="text" ref={this.kw} onKeyPress={this.handleEnterKey} placeholder="请输入演出名称"/>
            <button onClick={this.getResult.bind(this)}>搜索</button>
        </div>
    }
}

export default SearchPage
