import React, { Component } from 'react';
import axios from 'axios'
import {withRouter} from 'react-router-dom';
import { Grid } from 'antd-mobile';

import '../../style/home.scss'

class List extends Component {
    constructor(props) { 
        super(props); 
        this.state = {
            //热门门票
            hotTicket:[],
        }
    }
    componentWillMount(){
		axios.get('/myapi/wapapi/hbztj.do',{
			params:{
                cityid:1,
				sign:'bb4284fa771000e8c0',
				time:1543729843
			}
		}).then(res=>{
            let data = res.data;
            console.log(data)  
			this.setState({
				hotTicket:data,
            });
        });
    }
    handlerGotoDetails(hotTicket,idx){
        //获取history
        // console.log(this.props);
        let {history} = this.props;
        // console.log(history);
        var pid = hotTicket.urllink.split('/')[5]
        pid = pid.split('.')[0]
        // console.log(pid)
        history.push({
            pathname:'/detail/'+hotTicket.cid+'/'+pid,
        });
    }
  render() {
      let data = this.state.hotTicket
      console.log(data)
    return (         
        <div className="list">
            <div>
                {data.map}
            </div>
            <Grid
                data={this.state.hotTicket} 
                columnNum={1} 
                activeClassName="active" 
                itemStyle={{height:'rem(375)'}}
                renderItem={(tickets,idx)=>{
                    return(
                        <div className="listInfo">
                            <ul>
                                <li >
                                    <img src={tickets.thumbsrc} className="fl" />
                                    <div className="info fl">
                                        <h4>{tickets.title}</h4>
                                        <p>{tickets.city}</p>
                                        <p>{tickets.price}</p>
                                        <span className="disc">售票中</span>
                                    </div>
                                </li>
                            </ul>
                        </div>
                    )
                }}
                onClick={this.handlerGotoDetails.bind(this)}
            />
        </div>
       
      

    );
  }
}

List = withRouter(List);
export default List;
