import React,{ Component } from 'react'
import { Accordion, List } from 'antd-mobile';
import {connect} from 'react-redux';
// import {shownav} from '../../actions';

import '../../style/common.scss'

class Nav extends Component {
    constructor(){
        super();
        this.state = {
            nav:[
                {title:'首页',con:[]},
                {title:'北京',con:[
                    '演唱会','音乐会/歌剧','话剧/舞台剧','音乐剧','戏曲曲艺','芭蕾舞/舞蹈',
                    '相声/小品','马戏/杂技/魔术','综艺节目',''
                ]},
                {title:'文艺演出',con:[]},
                {title:'体育赛事',con:[]},
                {title:'其他',con:[]},
            ]
        }
    }
    onChange = (key) => {
        console.log(key);
      }
    render(){
        return (
            <div className="nav">
                 <div className="navList">
                    <div style={{ marginTop: 10, marginBottom: 10 }}>
                        <Accordion 
                        accordion={true}
                        openAnimation={{}} 
                        className="my-accordion" 
                        onChange={this.onChange}
                        >
                            <Accordion.Panel header="首页" className="pad"></Accordion.Panel>
                            <Accordion.Panel header="北京" className="pad"></Accordion.Panel>
                            <Accordion.Panel header="文艺演出" className="pad">
                                <List className="my-list">
                                    <List.Item className="item">演唱会</List.Item>
                                    <List.Item className="item">音乐会/歌剧</List.Item>
                                    <List.Item className="item">话剧/舞台剧</List.Item>
                                    <List.Item className="item">音乐剧</List.Item>
                                    <List.Item className="item">戏曲曲艺</List.Item>
                                    <List.Item className="item">芭蕾舞/舞蹈</List.Item>
                                    <List.Item className="item">相声/小品</List.Item>
                                    <List.Item className="item">马戏/杂技/魔术</List.Item>
                                    <List.Item className="item">综艺节目</List.Item>
                                    <List.Item className="item">亲子家庭</List.Item>
                                </List>
                            </Accordion.Panel>
                            {/* <Accordion.Panel header="体育赛事" className="pad">
                            <List className="Sports-list">
                                    <List.Item className="item">演唱会</List.Item>
                                    <List.Item className="item">音乐会/歌剧</List.Item>
                                    <List.Item className="item">话剧/舞台剧</List.Item>
                                    <List.Item className="item">音乐剧</List.Item>
                                    <List.Item className="item">戏曲曲艺</List.Item>
                                    <List.Item className="item">芭蕾舞/舞蹈</List.Item>
                                    <List.Item className="item">相声/小品</List.Item>
                                </List>
                            </Accordion.Panel>
                            <Accordion.Panel header="其他" className="pad">
                            <List className="other-list">
                                    <List.Item className="item">演唱会</List.Item>
                                    <List.Item className="item">音乐会/歌剧</List.Item>
                                    <List.Item className="item">话剧/舞台剧</List.Item>
                                    <List.Item className="item">音乐剧</List.Item>
                                    <List.Item className="item">戏曲曲艺</List.Item>
                                    <List.Item className="item">芭蕾舞/舞蹈</List.Item>
                                </List>
                            </Accordion.Panel> */}
                        </Accordion>
                    </div>
                 </div>
                <div className="navBtn">
                    <button>登录</button>
                    <p>客服电话：400-880-2880</p>
                </div>
            </div>
        )
    }

   
}


let mapStateToProps = state=>{
    // 此处必须返回一个对象
    console.log('oldState:'+state);
    return {
        //把state.commonReducer.shownavStatus映射到props
        shownavStatus:state.commonReducer.shownavStatus,
    }
  }
  
  Nav = connect(mapStateToProps)(Nav);


export default Nav;
