import React,{Component} from 'react';
import { Carousel } from 'antd-mobile';
import axios from 'axios';
import '../../../style/home.scss'
import { faDivide } from '../../../../node_modules/@fortawesome/free-solid-svg-icons';

class Banner extends Component {
    constructor(){
        super();
        this.state = {
            //轮播图商品
            ad:[],
        }
    }
    componentWillMount(){
		axios.get('/myapi/wapapi/hdpic.do',{
			params:{
                cityid:1,
				sign:'bb4284fa771000e8c0',
				time:1543729843
			}
		}).then(res=>{
            let data = res.data;
			this.setState({
				ad:data,
			});
        });
    }
    render() {
      return (
       <div>
            <Carousel
            autoplay={true}
            infinite
            >
                {this.state.ad.map(bans => (
                    <a
                    key={bans.id}
                    style={{height:'320px'}}
                    >
                    <img
                        src={bans.thumbsrc}
                        style={{ width: '100%', height:'207px', verticalAlign: 'top' }}
                        onLoad={() => {
                            window.dispatchEvent(new Event('resize'));
                        }}
                    />
                    </a>
                ))}
            </Carousel>
       </div>
      );
    }
  }

export default Banner;
