import React,{ Component } from 'react'
import { Accordion, List } from 'antd-mobile';
import {withRouter} from 'react-router-dom'

import {connect} from 'react-redux';


import '../../style/common.scss'


class Nav extends Component {
    constructor(){
        super();
        this.state = {
            nav:[
                {title:'文艺演出',con:[
                    '演唱会','音乐会/歌剧','话剧/舞台剧','音乐剧','戏曲曲艺','芭蕾舞/舞蹈',
                    '相声/小品','马戏/杂技/魔术','综艺节目','亲子家庭'
                ]},
                {title:'体育赛事',con:[
                    '演唱会','音乐会/歌剧','话剧/舞台剧','音乐剧','戏曲曲艺','芭蕾舞/舞蹈',
                    '相声/小品'
                ]},
                {title:'其他',con:[
                    '演唱会','音乐会/歌剧','话剧/舞台剧','音乐剧','戏曲曲艺','芭蕾舞/舞蹈'
                ]},
            ]
        }
    }
    gotopage = (key) => {
        console.log(key);
      }
    render(){
        let navCon = this.state.nav   
        return (
            <div className="nav">
                <div className="navList">
                <Accordion defaultActiveKey="0" className="my-accordion" onChange={this.gotopage}>
                    {navCon.map(navCon=>{
                        return <div>
                            <Accordion.Panel header="首页"></Accordion.Panel>
                        <Accordion.Panel header="北京"></Accordion.Panel>
                        <Accordion.Panel header={navCon.title}>
                            <List className="my-list">
                                <List.Item>{navCon.con}</List.Item>
                            </List>
                    </Accordion.Panel>
                        </div>
                    })
                }
                </Accordion>
                </div>
                <div className="navBtn">
                    <button>登录</button>
                    <p>客服电话：400-880-2880</p>
                </div>
            </div>
        )
    }
}

let mapStateToProps = state=>{
    // 此处必须返回一个对象
    return {
        //把state.commonReducer.tabbarStatus映射到props
        showStatus:state.commonReducer.shownavStatus,
    }
}

Nav = connect(mapStateToProps)(Nav);

Nav = withRouter(Nav)
export default Nav;
