import React,{Component} from 'react';
import '../../style/home.scss'
import store from '../../store';
import {connect} from 'react-redux';
import {shownav} from '../../actions';
import { library } from '@fortawesome/fontawesome-svg-core'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBars,faSearch } from '@fortawesome/free-solid-svg-icons'


library.add(faBars,faSearch)
// console.log(this.props);
// console.log("old:"+store.getState);

class Header extends Component {
    showNav(){
        // store.dispatch(shownavStatus(!store.getState))
        // console.log("new:"+store.getState);
        // store.subscribe(()=>{
        //     console.log("修改"+store.getState);
        // })
    }
    handlerGotoList(hotTicket,idx){
        //获取history
        // console.log(this.props);
        let {history} = this.props;
        // console.log(history);
        var pid = hotTicket.urllink.split('/')[5]
        pid = pid.split('.')[0]
        // console.log(pid)
        history.push({
            pathname:'/detail/'+hotTicket.cid+'/'+pid,
        });
    }
    render(){
        return (
            <div className="header">
                <div className="nav-icon" onClick={this.showNav.bind(this)}> <FontAwesomeIcon icon="bars" /></div>
                <div className="header-title"><h3>中票在线<span>北京</span></h3></div>
                <div className="search-icon" onClick={this.handlerGotoList.bind(this)}><FontAwesomeIcon icon="search" /></div>
            </div>
        )
    }
}

// let mapStateToProps=state=>({isShow:state.});
let mapDispatchToProps = dispatch=>{
    console.log(dispatch)
    return {
        // 把changeShowNav方法映射到props
        changeShowNav(status){
            dispatch(shownav(status));
        }
    }
}
Header = connect(mapDispatchToProps)(Header);

export default Header
