import React, { Component } from 'react';
import axios from 'axios'
import {withRouter} from 'react-router-dom';
import { Grid } from 'antd-mobile';

import '../../../style/home.scss'

class TacketList extends Component {
    constructor(){
        super();
        this.state = {
            //热门门票
            hotTicket:[],
            pid:[]
        }
    }
    componentWillMount(){
		axios.get('/myapi/wapapi/hbztj.do',{
			params:{
                cityid:1,
				sign:'bb4284fa771000e8c0',
				time:1543729843
			}
		}).then(res=>{
            let data = res.data;
            console.log(typeof(data),data)  
            // console.log(data.urllink) 
			this.setState({
				hotTicket:data,
            });
            
            let pid1 = data.map((val,idx)=>{
                return  val
           })
           this.setState({
            pid:pid1,
        });
        //    let p=pid1.map(val=>console.log(val.urllink));
        //    let pid = pid1.split(',')
        //    console.log(pid1[0].urllink) 
        //    let pid3 = JSON.stringify(pid1)
        //    console.log(typeof(pid3))

        //    let pid2 = pid3.split('[')[1].trim()
        //    let pid4 = pid3.split(',')[7]
        //     console.log(pid4) 
        });
    }
    handlerGotoDetails(hotTicket,idx){
        //获取history
        console.log(this.props)
        let {history} = this.props;
        console.log(history);
       
        history.push({
            pathname:'/detail/'+hotTicket.cid+'/'+hotTicket.pid,
        });
    }
    btn(val){
        console.log(this)
    }
  render() {
    return (         
        <div className="tacketList">
            <div className="listTitle">
                <i></i>
                <h3>热门</h3>
            </div>
            <Grid
                data={this.state.hotTicket} 
                columnNum={1} 
                activeClassName="active" 
                itemStyle={{height:'rem(375)'}}
                renderItem={(tickets,idx)=>{
                    return(
                        <div className="listInfo">
                            <ul>
                                {console.log(tickets.urllink)}
                                <li name={tickets.urllink}  onClick={this.btn.bind(this,tickets.urllink)}>
                                    <img src={tickets.thumbsrc} className="fl" />
                                    <div className="info fl">
                                        <h4>{tickets.title}</h4>
                                        <p>{tickets.city}</p>
                                        <p>{tickets.price}</p>
                                        <span className="disc">售票中</span>
                                    </div>
                                </li>
                            </ul>
                        </div>
                    )
                }}
                onClick={this.handlerGotoDetails.bind(this)}
            />
        </div>
       
      

    );
  }
}

TacketList = withRouter(TacketList);
export default TacketList;
