import React,{Component} from 'react'
import {withRouter} from 'react-router-dom'
import axios from 'axios';

class SearchPage extends Component{
    constructor(){
        super()
        this.state = {
            result:[],
            page:1
        }
    }
    getResult(){
        let kw = this.input.value;
        let page = 
        axios.get('/myapi/wapapi/searchshows.do',{
			params:{
                cityid:1,
                kw:kw,
                page:1,
				sign:'1ef59d37454f6ea3ab',
				time:1544148448
			}
		}).then(res=>{
            let data = res.data.datas;
            // console.log(data)  
			this.setState({
				result:data,
            });
            this.toFather()
        });
    }
    toFather(){
        // console.log(this.state.result) 
        this.props.onValue(this.state.result)
    }
    handleEnterKey = (e) => {
        if(e.nativeEvent.keyCode === 13){
             this.getResult()
        }
    }
    render(){
        return <div className="searchPage">
            <input type="text" ref={input => this.input = input} onKeyPress={this.handleEnterKey.bind(this)} placeholder="请输入演出名称"/>
            <button onClick={this.getResult.bind(this)}>搜索</button>
        </div>
    }
}

SearchPage = withRouter(SearchPage);
export default SearchPage
