import React,{Component} from 'react'
import {withRouter} from 'react-router-dom'

class SearchPage extends Component{
    constructor(props){
        super(props)
        this.state = {
            keyWord:''
        }
    }
    toFather(){
        let keyWord = this.input.value;
        // console.log(keyWord);
        this.setState({
            keyWord:keyWord,
        });
        // console.log(this.props);
        this.props.onValue(keyWord)
    }
    handleEnterKey = (e) => {
        if(e.nativeEvent.keyCode === 13){
             this.toFather()
        }
    }
    render(){
        return <div className="searchPage">
            <input type="text" ref={input => this.input = input} onKeyPress={this.handleEnterKey.bind(this)} placeholder="请输入演出名称"/>
            <button onClick={this.toFather.bind(this)}>搜索</button>
        </div>
    }
}

SearchPage = withRouter(SearchPage);
export default SearchPage
