import React,{ Component } from 'react'
import { Accordion, List } from 'antd-mobile';
import {connect} from 'react-redux';
// import {shownav} from '../../actions';

import '../../style/common.scss'

class Nav extends Component {
    constructor(){
        super();
        this.state = {
            nav:[
                {title:'首页',con:[]},
                {title:'北京',con:[]},
                {title:'文艺演出',con:[
                    '演唱会','音乐会/歌剧','话剧/舞台剧','音乐剧','戏曲曲艺','芭蕾舞/舞蹈',
                    '相声/小品','马戏/杂技/魔术','综艺节目','亲子家庭'
                ]},
                {title:'体育赛事',con:[
                    '演唱会','音乐会/歌剧','话剧/舞台剧','音乐剧','戏曲曲艺','芭蕾舞/舞蹈',
                    '相声/小品'
                ]},
                {title:'其他',con:[
                    '演唱会','音乐会/歌剧','话剧/舞台剧','音乐剧','戏曲曲艺','芭蕾舞/舞蹈'
                ]},
            ]
        }
    }
    onChange = (key) => {
        console.log(key);
      }
    render(){
        let navCon = this.state.nav
        console.log(navCon)
        return (
            <div className="nav">
                <div className="navList">
                    <Accordion 
                    accordion={true}
                    openAnimation={{}} 
                    className="my-accordion" 
                    onChange={this.onChange}
                    >
                    {
                this.state.menu.map(item=>{
                    //key遵循的原则：唯一、稳定
                    return <li key={item.path} onClick={this.handlerClick.bind(this,item)}>{item.title}</li>
                })
            }
                        
                    </Accordion>
                </div>
                <div className="navBtn">
                    <button>登录</button>
                    <p>客服电话：400-880-2880</p>
                </div>
            </div>
        )
    }

   
}


let mapStateToProps = state=>{
    // 此处必须返回一个对象
    console.log('oldState:'+state);
    return {
        //把state.commonReducer.shownavStatus映射到props
        shownavStatus:state.commonReducer.shownavStatus,
    }
  }
  
  Nav = connect(mapStateToProps)(Nav);


export default Nav;
