import React,{Component} from 'react';
import '../../style/home.scss'
import store from '../../store';
import {connect} from 'react-redux';
import { library } from '@fortawesome/fontawesome-svg-core'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBars,faSearch } from '@fortawesome/free-solid-svg-icons'

library.add(faBars,faSearch)

console.log("old:"+store.getState);

class Header extends Component {
    showNav(){
        store.dispatch(shownavStatus(!store.getState))
        console.log("new:"+store.getState);
        // store.subscribe(()=>{
        //     console.log("修改"+store.getState);
        // })
    }
    render(){
        return (
            <div className="header">
                <div className="nav-icon" onClick={this.showNav.bind(this)}> <FontAwesomeIcon icon="bars" /></div>
                <div className="header-title"><h3>中票在线<span>北京</span></h3></div>
                <div className="search-icon"><FontAwesomeIcon icon="search" /></div>
            </div>
        )
    }
}

let mapStateToProps=state=>({shownavStatus:state.cartReducer.goodslist});
let mapDispatchToProps = dispatch=>{
    return {
        // 把changeShowNav方法映射到props
        changeShowNav(status){
            dispatch(shownav(status));
        }
    }
}
Header = connect(mapStateToProps,mapDispatchToProps)(Header);

export default Header
