import React,{Component} from 'react'
import {withRouter} from 'react-router-dom'
import SearchHeader from './SearchHeader';
import SearchPage from './SearchPage';
import List from '../../commons/List';

import '../../../style/search.scss'
import '../../../style/detail.scss'


class Search extends Component{
    constructor(){
        super()
        this.state={
            result:[],
            page:1
        }
    }

    fa(res) {
        this.setState({
            result: res, //往父组件中的result传入子组件传递的值 
        },() =>{
           console.log(this.state.result);//setState是异步操作，但是我们可以在它的回调函数里面进行操作
        });
 
    }
    fb(page) {
        this.setState({
            page: page, //往父组件中的result传入子组件传递的值 
        },() =>{
           console.log(this.state.page);//setState是异步操作，但是我们可以在它的回调函数里面进行操作
        });
 
    }
    
    render(){
        return (
            <div className="search">
                <SearchHeader></SearchHeader>
                <SearchPage onValue={this.fa.bind(this)} page={this.state.page} ></SearchPage>
                <List result={this.state.result} onAdd={this.fb.bind(this)}></List>
            </div>
        )
    }
}

Search=withRouter(Search)
export default Search
